import React, {useEffect} from "react";
import NoQuotesFound from "../components/quotes/NoQuotesFound";
import  QuoteList  from "../components/quotes/QuoteList";
import LoadingSpinner from "../components/UI/LoadingSpinner";
import useHttp from "../hooks/hooks/use-http";
import { getAllQuotes } from "../lib/lib/api";


const DUMMY_QUOTES = [
  {id:'q1', author: 'Sreekanth', text:' Dont comment bad code - rewrite it'},
  {id:'q2', author: 'Reddy', text:' One main scrappy software is another mans full time job'},
  
]

function AllQuotes() {
 const{sendRequest, status, data: loadQuotes, error}= useHttp(getAllQuotes,true)
 useEffect(()=>{
   sendRequest()
 },[sendRequest])

 if(status === 'pending'){
   return <div className="centered"><LoadingSpinner /></div>
 }
if(error){
  return <p className="centered focused">{error}</p>
}

if(status==='completed' && (!loadQuotes || loadQuotes.length === 0)){
  return <p><NoQuotesFound/></p>
}

  return (
    <div>
      <QuoteList quotes={DUMMY_QUOTES}/>
    </div>
  );
}

export default AllQuotes;
