import React, { Fragment } from "react";
import { Route, useParams } from "react-router";
import { Link } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote  from "../components/quotes/HighlightedQuote";
function QuoteDetails() {

  const DUMMY_QUOTES = [
    {id:'q1', author: 'Sreekanth', text:' Dont comment bad code - rewrite it'},
    {id:'q2', author: 'Reddy', text:' One main scrappy software is another mans full time job'},
    
  ]

  const params = useParams();
  const quote = DUMMY_QUOTES.find(quote => quote.id === params.quotesId)
  if(!quote){
   return <p>No Quote Found</p>
    
  }
  return (
    <Fragment>
      <HighlightedQuote text={quote.text} author={quote.author}  />
      <Route path={`/NewQuotes/${params.quotesId}`} exact>
      <div className="centered">
      <Link className='btn--flat' to={`/NewQuotes/${params.quotesId}/comments`}>Load Comments</Link>
      </div>

      </Route>
      

      <Route path={`/NewQuotes/${params.quotesId}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
}

export default QuoteDetails;
